mapper.insert:
insert into user_profile (id, email, username, 
  password, gender, tel, 
  image, create_time, rate
  )
values (#{id,jdbcType=INTEGER}, #{email,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{gender,jdbcType=TINYINT}, #{tel,jdbcType=VARCHAR}, 
  #{image,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{rate,jdbcType=DECIMAL}
  )

mapper.select:
select count(1)
from user_profile
where email=#{email}

select 
from user_profile
where email=#{email}
and password=#{password}
